import React from 'react';

function Slides({slides}) {

    const [activeIndex, setActiveIndex] = useState(0)
    const [currentSlide, setCurrentSlide] = useState(slide[0])


    const onClickNext = () => {
        var currSlideIndex = activeIndex;

        if(currSlideIndex < slides.length - 1){
            currSlideIndex++;
            setActiveIndex(currSlideIndex);
            setCurrentSlide(slides[currSlideIndex]);
        }
    }

    const onClickPrev = () => {
        var currSlideIndex = activeIndex;

        if (currSlideIndex > 0){
            currSlideIndex--;
            setActiveIndex(currSlideIndex);
            setCurrentSlide(slides[currSlideIndex]);
        }
    }


    return (
        <div>
            <div id="navigation" className="text-center">
                <button data-testid="button-restart" className="small outlined">Restart</button>
                <button data-testid="button-prev" className="small">Prev</button>
                <button data-testid="button-next" className="small">Next</button>
            </div>
            <div id="slide" className="card text-center">
                <h1 data-testid="title">Slide Title Here</h1>
                <p data-testid="text">Slide Text Here</p>
            </div>
        </div>
    );

}

export default Slides;
